import { StyleSheet } from "react-native";

export default StyleSheet.create({
  imagem: {
    flex: 2,
    width: 390,
    height: 900
  },
  image: {
    width: "49px",
    height: "50px",
    margin: "5px",
    marginEnd: "10px"
  },

  txtAtt: {
    padding: "5px",
    border: "1px solid",
    borderRadius: '10px',
    margin: "5px",
    backgroundColor: "#ddd",
  },
  divTxtAtt: {
    borderBottomLeftRadius: '10px',
    borderBottomRightRadius: '10px',
    alignItems: "left",
    marginBottom: "5px",
    position: 'relative',
    backgroundColor: "#ddd",
  },
  dados: {
    margin: "10px"
  },
  turma: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-beteween",
    backgroundColor: "#fff",
    borderRadius: "10px"
  },
});
