import { createDrawerNavigator } from '@react-navigation/drawer';
import { Text, Image } from 'react-native';

const Drawer = createDrawerNavigator();

import Professor from '../../pages/professorHome/professorHome';
import Aluno from '../../pages/alunoHome/alunoHome';

export default function Menu({ navigation }) {
    var user = JSON.parse(localStorage.getItem('nome'))
    console.log(user)
    return (
        <Drawer.Navigator screenOptions={{ headerShown: false }}>

            <Drawer.Screen name="Professor" component={Professor}
                options={{
                    drawerLabel: (() => <Text style={{ margin: '0px',  }}>Ol√°, {user.nome} </Text>),
                }}>
            </Drawer.Screen>
            <Drawer.Screen name="Home" component={Aluno}
                options={{
                    drawerLabel: (({ focused }) => <Text style={{ color: focused ? '#000' : '#aaa' }}>Home</Text>),
                    drawerIcon: (({ focused }) => <Image style={{ color: focused ? '#313131' : '#fff' }} source={require('../../../assets/fundo.jpg')} />)
                }}>
            </Drawer.Screen>
        </Drawer.Navigator>
    )
}
