import { Animated } from 'react-native';


export default function RotatingImage({ navigation }) {
    const rotationValue = new Animated.Value(0);

    // Função para iniciar a animação de rotação


    return (
        <Animated.Image
            source={require('../../../assets/setaCima.png')} on
            style={{
                transform: [
                    {
                        rotate: rotationValue.interpolate({
                            inputRange: [0, 1],
                            outputRange: ['0deg', '360deg'],
                        }),
                    },
                ],
                width: '10px',
                height: '10px',
                margin: '5px',
                position: 'absolute',
                right: 0,
            }}
        />
    );
};
