import { View, Text, TouchableOpacity, TextInput, ImageBackground } from 'react-native';
import { RadioButton } from "react-native-paper";
import styles from '../cadastro/style'

export default function Cadastro({ navigation }) {

    const [checked, setChecked] = useState("aluno");
    const [Nome, setNome] = useState("");
    const [Email, setEmail] = useState("");
    const [Senha, setSenha] = useState("");

    const voltar = () => {
        navigation.navigate('Login')
    }

    function cadastrar() {
        let form = {
            nome: Nome,
            email: Email,
            senha: Senha,
        };

        let uri = checked === "professor" ? "http://localhost:3000/professores/create" : "http://localhost:3000/alunos/create";
        let path = checked === "Login";

        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(form),
        };

        fetch(uri, options)
            .then((resp) => {
                if (resp.ok) {
                    return resp.json();
                } else if (resp.status === 401) {
                    setMsg("Senha incorreta")
                    throw new Error("Senha incorreta");
                } else if (resp.status === 404) {
                    setMsg("Usuário não encontrado")
                    throw new Error("Usuário não encontrado");
                } else {
                    setMsg("Erro interno do servidor")
                    throw new Error("Erro interno do servidor");
                }
            })
            .then((data) => {
                console.log(data);
                AsyncStorage.setItem("nome", JSON.stringify(data.info));

                navigation.navigate(path);
            })
            .catch((error) => {
                console.error(error);
            });
    }

    return (
        <View style={styles.container}>
            <ImageBackground source={require("../../../assets/fundo.jpg")} resizeMode="cover" style={styles.imagem}></ImageBackground>
            <View style={styles.divizinha}>
                <Text style={styles.titulo}>Cadastro</Text>
                <TouchableOpacity onPress={() => { voltar() }}>
                    <Text style={styles.txtSair}>Sair</Text>
                </TouchableOpacity>
            </View>
            <View style={styles.dados}>
                <Text>Nome:</Text>
                <TextInput value={Nome} onChangeText={(val) => { setNome(val) }} style={styles.inputzinho}></TextInput>
                <Text>Email:</Text>
                <TextInput value={Email} onChangeText={(val) => { setEmail(val) }} style={styles.inputzinho}></TextInput>
                <Text>Senha:</Text>
                <TextInput value={Senha} onChangeText={(val) => { setSenha(val) }} secureTextEntry={true} style={styles.inputzinho}></TextInput>
                <View>
                    <Text style={styles.label}>
                        Sou:
                        <Text style={styles.label}>
                            <RadioButton value="aluno"
                                status={checked === "aluno" ? "checked" : "unchecked"}
                                onPress={() => setChecked("aluno")} />{" "}
                            Aluno(a)
                        </Text>
                        <Text style={styles.label}>
                            <RadioButton value="professor"
                                status={checked === "professor" ? "checked" : "unchecked"}
                                onPress={() => setChecked("professor")} />{" "}
                            Professor(a)
                        </Text>
                    </Text>
                </View>
            </View>
            <View style={styles.cadastro}>
                <TouchableOpacity style={styles.buttonzinho} onPress={() => { cadastrar() }}>
                    <Text style={styles.txtbutton}>Cadastrar</Text>
                </TouchableOpacity>
            </View>
        </View >
    );
}
