import { createDrawerNavigator } from '@react-navigation/drawer';
import { Text } from 'react-native';
import { Ionicons } from '@expo/vector-icons';

const Drawer = createDrawerNavigator();

import Perfil from '../../pages/perfil/perfil';
import Aluno from '../../pages/alunoHome/alunoHome';

 function login() {
        let form = {
            email: Email,
            senha: Senha,
        };

        let uri =
            checked === "professor" ? "http://localhost:3000/professores/login" : "http://localhost:3000/alunos/login";
        let path = checked === "professor" ? "MenuProfessor" : "MenuAluno";

        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(form),
        };

        fetch(uri, options)
            .then((resp) => {
                if (resp.ok) {
                    return resp.json();
                } else if (resp.status === 401) {
                    setMsg("Senha incorreta")
                    throw new Error("Senha incorreta");
                } else if (resp.status === 404) {
                    setMsg("Usuário não encontrado")
                    throw new Error("Usuário não encontrado");
                } else {
                    setMsg("Erro interno do servidor")
                    throw new Error("Erro interno do servidor");
                }
            })
            .then((data) => {
                console.log(data);

                AsyncStorage.setItem("nome", JSON.stringify(data.info));
                AsyncStorage.setItem("id_aluno", data.info.id_aluno);
                AsyncStorage.setItem("token", data.token);

                navigation.navigate(path);
            })
            .catch((error) => {
                console.error(error);
            });
    }

export default function Menu({ navigation }) {
    var user = JSON.parse(localStorage.getItem('nome'))
    return (
        <Drawer.Navigator screenOptions={{ headerShown: false }} initialRouteName='Home'>
            <Drawer.Screen name="Perfil" component={Perfil}
                options={{
                    drawerLabel: (() => <Text
                    // style={{ padding: '20px', margin: '0px', border: '1px solid black' }}
                    > Olá, {user.nome} </Text>),
                }}>
            </Drawer.Screen>
            <Drawer.Screen name="Home" component={Aluno}
                options={{
                    drawerLabel: (({ focused }) => <Text style={{ color: focused ? '#000' : '#aaa' }}>Home</Text>),
                    drawerIcon: ({ focused, color, size }) => (<Ionicons name={focused ? 'home' : 'home-outline'} size={size} color={color} />),
                }}>
            </Drawer.Screen>
        </Drawer.Navigator>
    )
}
