import { View, Text, TouchableOpacity, Image, ImageBackground } from 'react-native';
import styles from '../perfil/style'

export default function Perfil({ navigation }) {

    const menu = () => {
        navigation.openDrawer();
    }

    var user = JSON.parse(localStorage.getItem('nome'))
    var id_aluno = JSON.parse(localStorage.getItem('id_aluno'))
    

    function login() {
        let form = {
            email: Email,
            senha: Senha,
        };

        let uri =
            checked === "professor" ? "http://localhost:3000/readOne/:id_prof" : "http://localhost:3000/readOne/:id_aluno";
        let path = checked === "professor" ? "MenuProfessor" : "MenuAluno";

        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(form),
        };

        fetch(uri, options)
            .then((resp) => {
                if (resp.ok) {
                    return resp.json();
                } else if (resp.status === 401) {
                    setMsg("Senha incorreta")
                    throw new Error("Senha incorreta");
                } else if (resp.status === 404) {
                    setMsg("Usuário não encontrado")
                    throw new Error("Usuário não encontrado");
                } else {
                    setMsg("Erro interno do servidor")
                    throw new Error("Erro interno do servidor");
                }
            })
            .then((data) => {
                console.log(data);

                AsyncStorage.setItem("nome", JSON.stringify(data.info));
                AsyncStorage.setItem("id_aluno", data.info.id_aluno);
                AsyncStorage.setItem("token", data.token);

                navigation.navigate(path);
            })
            .catch((error) => {
                console.error(error);
            });
    }

    return (
        <View>
            <View >
                <TouchableOpacity onPress={() => { menu() }}>
                    <Image style={styles.image} source={require('../../../assets/favicon.png')} />
                </TouchableOpacity>
                <Text> Perfilzinho </Text>
            </View>
        </View>
    )
}
