import AsyncStorage from "@react-native-async-storage/async-storage";
import { useState, useEffect } from 'react'
import { View, Text, Modal, TouchableOpacity, Image, ImageBackground, TextInput, Alert } from 'react-native';
import styles from '../alunoHome/style'
import CardAlunoHome from '../../../components/cardAlunoHome/cardAlunoHome'

export default function Atividade({ navigation }) {

    const menu = () => {
        clearInterval(myInterval);
        navigation.openDrawer();
    };

    const voltar = () => {
        clearInterval(myInterval);
        navigation.navigate("Login");
    };

    async function dados() {
        try {
            const userString = await AsyncStorage.getItem("nome");
            if (userString) {
                const user = JSON.parse(userString);
                const token = user.token;
                const id_aluno = user.id_aluno;
                fetch("http://localhost:3000/alunos/readOne/" + id_aluno)
                    .then((resp) => {
                        return resp.json();
                    })
                    .then((data) => {
                        setInfo(data);
                        // setId_turma(data.turma[0].id_turma);
                        console.log();
                    });
            }
        } catch (error) {
            console.error("Error:", error);
        }
    }

    return (
        <View style={styles.container}>
            <Modal visible={modalVisible} animationType="slide" transparent>
                <ModalContent />
            </Modal>
            <ImageBackground source={require('../../../../assets/fundo.jpg')} resizeMode="cover" style={styles.imagem}></ImageBackground>
            <View style={styles.divizinha}>
                <TouchableOpacity style={styles.imagenzinha} onPress={() => { menu() }}>
                    <Image style={styles.image} source={require('../../../../assets/favicon.png')} />
                </TouchableOpacity>
                    <Text style={styles.txtEntrar}> Atividades </Text>
                <TouchableOpacity onPress={() => { voltar() }}>
                    <Text style={styles.txtSair}>Sair</Text>
                </TouchableOpacity>
            </View>
        </View>
    )
}
